// --- file[capitalize.js] ---

'use strict';

/**
 * @ngdoc filter
 * @name dashApp.filter:capitalize
 * @function
 * @description
 * # capitalize
 * Filter in the dashApp.
 */
angular.module('dashApp')
  .filter('capitalize', function () {
    return function (input) {
      return input.substring(0,1).toUpperCase()+input.substring(1);
    };
  });



// --- file[urlify.js] ---

'use strict';

angular.module('dashApp')
  .filter('urlify', function () {
    return function (input) {
      if (input.indexOf("/") > -1) {
          var new_input = input.replace('/','');
          return new_input;
      }
      else{
        return input;
      }
    };
  });



